-- Create databases
CREATE DATABASE msaportal;
CREATE DATABASE reservation;

-- Connect to reservation database
\c reservation

-- Create u_reserve table
CREATE TABLE u_reserve (
    reserve_id VARCHAR(255) NOT NULL,
    reserve_item_id BIGINT,
    location_id BIGINT,
    category_id VARCHAR(255),
    reserve_qty BIGINT,
    reserve_purpose_content VARCHAR(4000),
    attachment_code VARCHAR(255),
    reserve_start_date TIMESTAMP,
    reserve_end_date TIMESTAMP,
    reserve_status_id VARCHAR(20),
    reason_cancel_content VARCHAR(4000),
    user_id VARCHAR(255),
    user_contact_no VARCHAR(50),
    user_email_addr VARCHAR(500),
    create_date TIMESTAMP,
    created_by VARCHAR(255),
    modified_date TIMESTAMP,
    last_modified_by VARCHAR(255),
    PRIMARY KEY (reserve_id)
);

COMMENT ON TABLE u_reserve IS '예약 신청&확인';
COMMENT ON COLUMN u_reserve.reserve_id IS '예약 id';
COMMENT ON COLUMN u_reserve.reserve_item_id IS '예약 물품 id';
COMMENT ON COLUMN reserve.location_id IS '예약 물품-지역 id';
COMMENT ON COLUMN reserve.category_id IS '예약 물품-유형 id';
COMMENT ON COLUMN reserve.reserve_qty IS '예약 신청인원/수량';
COMMENT ON COLUMN reserve.reserve_purpose_content IS '예약신청 목적';
COMMENT ON COLUMN reserve.attachment_code IS '첨부파일 코드';
COMMENT ON COLUMN reserve.reserve_start_date IS '예약 신청 시작일';
COMMENT ON COLUMN reserve.reserve_end_date IS '예약 신청 종료일';
COMMENT ON COLUMN reserve.reserve_status_id IS '예약상태 - 공통코드(reserve-status)';
COMMENT ON COLUMN reserve.reason_cancel_content IS '예약 취소 사유';
COMMENT ON COLUMN reserve.user_id IS '예약자 id';
COMMENT ON COLUMN reserve.user_contact_no IS '예약자 연락처';
COMMENT ON COLUMN reserve.user_email_addr IS '예약자 이메일';
COMMENT ON COLUMN reserve.create_date IS '생성일';
COMMENT ON COLUMN reserve.created_by IS '생성자';
COMMENT ON COLUMN reserve.modified_date IS '수정일';
COMMENT ON COLUMN reserve.last_modified_by IS '수정자';

-- Connect to msaportal database
\c msaportal

-- Create api_log table
CREATE TABLE api_log (
                         log_id BIGSERIAL PRIMARY KEY,
                         site_id BIGINT,
                         user_id VARCHAR(255),
                         http_method VARCHAR(10),
                         request_url VARCHAR(500),
                         ip_addr VARCHAR(100),
                         created_date TIMESTAMP,
                         modified_date TIMESTAMP
);

        COMMENT ON TABLE api_log IS 'API access log';
        COMMENT ON COLUMN api_log.log_id IS '로그 id';
        COMMENT ON COLUMN api_log.site_id IS '사이트 id';
        COMMENT ON COLUMN api_log.user_id IS '사용자 id';
        COMMENT ON COLUMN api_log.http_method IS 'api http method';
        COMMENT ON COLUMN api_log.request_url IS '요청 url';
        COMMENT ON COLUMN api_log.ip_addr IS 'ip 주소';
        COMMENT ON COLUMN api_log.created_date IS '생성일시';
        COMMENT ON COLUMN api_log.modified_date IS '수정일시';

-- Create attachment table
CREATE TABLE attachment (
                            attachment_code VARCHAR(20) NOT NULL,
                            attachment_seq BIGINT NOT NULL,
                            download_count BIGINT DEFAULT 0,
                            entity_id VARCHAR(50),
                            entity_name VARCHAR(200),
                            file_type_value VARCHAR(100),
                            delete_at BOOLEAN DEFAULT false,
                            original_file_name VARCHAR(200) NOT NULL,
                            physical_file_name VARCHAR(200) NOT NULL,
                            attachment_size BIGINT,
                            attachment_id VARCHAR(50) NOT NULL,
                            created_date TIMESTAMP,
                            modified_date TIMESTAMP,
                            created_by VARCHAR(255),
                            last_modified_by VARCHAR(255),
                            PRIMARY KEY (attachment_code, attachment_seq)
);

CREATE UNIQUE INDEX attachment_id_uk ON attachment (attachment_id);

        COMMENT ON TABLE attachment IS '첨부파일';
        COMMENT ON COLUMN attachment.attachment_code IS '첨부파일 코드';
        COMMENT ON COLUMN attachment.attachment_seq IS '첨부파일 순번';
        COMMENT ON COLUMN attachment.download_count IS '다운로드 수';
        COMMENT ON COLUMN attachment.entity_id IS '연결 도메인 id';
        COMMENT ON COLUMN attachment.entity_name IS '연결 도메인 명';
        COMMENT ON COLUMN attachment.file_type_value IS '첨부파일 타입';
        COMMENT ON COLUMN attachment.delete_at IS '삭제 여부';
        COMMENT ON COLUMN attachment.original_file_name IS '원본 파일명';
        COMMENT ON COLUMN attachment.physical_file_name IS '물리 파일명';
        COMMENT ON COLUMN attachment.attachment_size IS '첨부파일 크기';
        COMMENT ON COLUMN attachment.attachment_id IS '첨부파일 id';
        COMMENT ON COLUMN attachment.created_date IS '생성 일시';
        COMMENT ON COLUMN attachment.modified_date IS '수정 일시';
        COMMENT ON COLUMN attachment.created_by IS '생성자 id';
        COMMENT ON COLUMN attachment.last_modified_by IS '수정자 id';

-- Create authorization table
-- 테이블 생성
CREATE TABLE u_authorization (
         authorization_no SERIAL PRIMARY KEY, -- 인가 번호
         authorization_name VARCHAR(50) NOT NULL, -- 인가 명
         url_pattern_value VARCHAR(200) NOT NULL, -- URL 패턴 값
         http_method_code VARCHAR(20) NOT NULL, -- HTTP METHOD 값
         sort_seq INTEGER NOT NULL DEFAULT 99999, -- 정렬 순서
         created_by VARCHAR(255) NOT NULL, -- 생성자 ID
         created_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, -- 생성 일시
         last_modified_by VARCHAR(255) NOT NULL, -- 수정자 ID
         modified_date TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP -- 수정 일시
);

-- Create authorization table

CREATE UNIQUE INDEX u_authorization_uk01 ON u_authorization (url_pattern_value, http_method_code);

COMMENT ON TABLE u_authorization IS '인가';
COMMENT ON COLUMN u_authorization.authorization_no IS '인가 번호';
COMMENT ON COLUMN u_authorization.authorization_name IS '인가 명';
COMMENT ON COLUMN u_authorization.url_pattern_value IS 'URL 패턴 값';
COMMENT ON COLUMN u_authorization.http_method_code IS 'HTTP METHOD 값';
COMMENT ON COLUMN u_authorization.sort_seq IS '정렬 순서';
COMMENT ON COLUMN u_authorization.created_by IS '생성자 id';
COMMENT ON COLUMN u_authorization.created_date IS '생성 일시';
COMMENT ON COLUMN u_authorization.last_modified_by IS '수정자 id';
COMMENT ON COLUMN u_authorization.modified_date IS '수정 일시';

